    <script>
        $(document).ready(function(){
            $("#new").change(function(){
                
                $("#choiceExisting").hide();
                $("#choiceNew").show();
                
                
            });
            $("#existing").change(function(){
                $("#choiceExisting").show();
                $("#choiceNew").hide();
            });

            $("#employees").change(function(){
                let id = parseInt($("#employees").val());
                var employees = {!! json_encode($employees) !!}
                
                let salary = 0;
                let email = "";
                employees.map((emp) => {
                                        if(emp.id === id)
                                        {
                                            salary = emp.salary;
                                            email = emp.email;
                                        }
                                    })
                console.log(salary);
                $("#salary").val(salary);
                $("#email").val(email);

            });
        });

    </script>
